INSERT INTO `ri_catalog_product_entity_text` VALUES ('619', '4', '73', '3', '8', 'Torre Cittadina, situata all\'interno dell\'area pedonale del Korzo, simbolo riconoscibile di Rijeka. Oggetto di ceramica, altezza 13 cm, larghezza 10 cm, con iscrizione ovale di ceramica RIJEKA nell\'angolo in alto a destra, su sfondo nero, con cornice di legno color verde delle dimensioni di 23 cm x 27 cm x 5,5 cm.\r\n\r\nOpera autentica d\'artista, realizzata con la tecnica originale di lavorazione a mano della ceramica. Il prodotto è stato cotto a fuoco per due volte, ad una temperatura di 1000 °C.\r\nIl prodotto ottenuto è il frutto di una tradizione trentennale di lavorazione della ceramica, dell\'esperienza e dell\'originalità dell\'artista Vladimir Bruketa, laureato presso l\'Hammersmith College of Art di Londra, nonché di altri membri della famiglia Bruketa.');
INSERT INTO `ri_catalog_product_entity_text` VALUES ('620', '4', '74', '3', '8', 'Torre Cittadina, situata all\'interno dell\'area pedonale del Korzo, simbolo riconoscibile di Rijeka. Oggetto di ceramica, altezza 13 cm, larghezza 10 cm, con iscrizione ovale di ceramica RIJEKA nell\'angolo in alto a destra, su sfondo nero, con cornice di legno color verde delle dimensioni di 23 cm x 27 cm x 5,5 cm. ');
